# identify at which position a message start after a start codon
msg = ""


def find_msg_start(msg, codon_length):
    codon = msg[:codon_length]
    for i, char in enumerate(msg[codon_length:], start=0):
        if len(set(codon)) == codon_length:
            break
        codon = codon[1:] + char

    pos_msg = codon_length + i
    return pos_msg


# Part 1
print(f"Codon starts at: {find_msg_start(msg, 4)}")

# Part 2
print(f"Codon starts at: {find_msg_start(msg, 14)}")

